const { useState } = React;
const { createRoot } = ReactDOM;

function App() {
  const [message, setMessage] = useState('World');

  return (
    <div>
      <h1>Hello {message}!</h1>
      <button onClick={() => setMessage('React')}>
        Click me
      </button>
    </div>
  );
}

const container = document.getElementById('root');
const root = createRoot(container);
root.render(<App />);
